import React from 'react';
import Header from './component/Header';
import { newsCategory } from './news';
import NewsList from './component/NewsList';
import Pagination from './component/Pagination';
import Loading from './component/Loading';
import axios from 'axios';



class App extends React.Component {

  state = {
    news: [],
    // category: '',
    // page: 1,
    // loading: false,
    // error: false,
    // errorMessage: ''
  }

  componentDidMount() {
    const url = `${process.env.REACT_APP_NEWS_API_URL}?apikey=${process.env.REACT_APP_NEWS_API_KEY}&category=technology&pageSize=5`;
    axios.get(url)
    .then(response => {  
      this.setState({ news: response.data.articles });
     console.log(response.data.articles);
    })
    .catch(error => {
      console.log(error);
    } 
    );
  }
  render ()

   {
    return(
      <div className="container">
        <div className="row">
          <div className="col-sm-6 offset-md-3">
           <Header category={newsCategory.technology} ></Header>
           <div className='d-flex'>
             <p className='text-black-50'>
               About {0} result found 
             </p>
             <p className='text-black-50 ml-auto'>
               {1} page of {100}
             </p>
           </div>
           {/* <NewsList news={this.state.news} ></NewsList> */}
           <Pagination></Pagination>
           <Loading></Loading>
          </div>
        </div>
      </div>

    )
  }

}

export default App;
