import React from 'react';
import Header from './component/Header';
import News, { newsCategory } from './news';
import NewsList from './component/NewsList';
import Pagination from './component/Pagination';
import Loading from './component/Loading';

const news = new News(newsCategory.technology);


class App extends React.Component {
  constructor() {
    super();
    this.state = {
      data: {},
      isLoading: true,
    }
  }
  componentDidMount() {
    news.getNews()
      .then(data => {
        this.setState({ data: data, isLoading: false })
      })
      .catch((e) => {
        console.log(e);
        alert("Something Was Wrong");
        this.setState({ isLoading: false })
      })

    // let news = new News(newsCategory.technology);
    // news.getNews().then(data =>{
    //   console.log(data);
    // })
    // const url = `${process.env.REACT_APP_NEWS_API_URL}?apikey=${process.env.REACT_APP_NEWS_API_KEY}&category=${this.state.category}&pageSize=5`;
    // axios.get(url)
    // .then(response => {  
    //   this.setState({ news: response.data.articles });
    //   console.log(this.state.news);
    // })
    // .catch(e => {
    //   console.log(e);
    // } 
    // );
  }

  next = () => {
    if (this.state.data.isNext) {
      this.setState({ isLoading: true });
    }
    news.nextPage().then(data => {
      this.setState({ data, isLoading: false })
    })
      .catch(e => {
        console.log(e);
        alert("Something Was Wrong in Next Page");
        this.setState({ isLoading: false })
      })

  }
  prev = () => {
    if (this.state.data.isPrev) {
      this.setState({ isLoading: true });
    }
    news.prevPage().then(data => {
      this.setState({ data, isLoading: false })
    })
      .catch(e => {
        console.log(e);
        alert("Something Was Wrong in Prev Page");
        this.setState({ isLoading: false })
      })
  }

  handlePageChanege = value => {
    this.setState({
      data: {
        ...this.state.data,
        currentPage: Number.parseInt(value),
      }
    })
  }
  goTOpage = () => {
    this.setState({ isLoading: true });
    news.setCurrentPage(this.state.data.currentPage).then(data => {
      this.setState({ data, isLoading: false });
      console.log(this.state.data.currentPage);
      console.log(data);
    })
      .catch(e => {
        console.log(e);
        alert("Something Was Wrong in Go tu page");
        this.setState({ isLoading: false });
      })
  }
  changeCategory = (category) => {
    this.setState({ isLoading: true });
    news.changeCategory(category).then(data => {
      this.setState({ data, isLoading: false })
    })
      .catch(e => {
        console.log(e);
        alert("Something Was Wrong in Change Category");
        this.setState({ isLoading: false })
      })
  }
  search = (search) => {
    this.setState({ isLoading: true });
    news.search(search).then(data => {
      this.setState({ data, isLoading: false })
    })
      .catch(e => {
        console.log(e);
        alert("Something Was Wrong in Search");
        this.setState({ isLoading: false })
      })
  }

  // componentDidUpdate(prevProps, prevState) {
  //   // if(prevState.category !== this.state.category){
  //   //   const url = `${process.env.REACT_APP_NEWS_API_URL}?apikey=${process.env.REACT_APP_NEWS_API_KEY}&category=${this.state.category}&pageSize=5`;
  //   //   axios.get(url)
  //   //   .then(response => {  
  //   //     this.setState({ news: response.data.articles });
  //   //     console.log(this.state.news);
  //   //   })
  //   //   .catch(e => {
  //   //     console.log(e);
  //   //   } 
  //   //   );
  //   // }
  // }
  render() {

    const {
      articles,
      totalPage,
      currentPage,
      category,
      totalResults,
      isNext,
      isPrev,
      isLoading
    } = this.state.data;

    return (

      <>
                  <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container-fluid">
              <a className="navbar-brand" href="#">Navbar</a>
              <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                <span className="navbar-toggler-icon"></span>
              </button>
              <div className="collapse navbar-collapse" id="navbarSupportedContent">
                <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                  <li className="nav-item">
                    <a className="nav-link active" aria-current="page" href="#">Home</a>
                  </li>
                  <li className="nav-item">
                    <a className="nav-link" href="#">Link</a>
                  </li>
                  <li className="nav-item dropdown">
                    <a className="nav-link dropdown-toggle" href="#" id="navbarDropdown" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                      Dropdown
                    </a>
                    <ul className="dropdown-menu" aria-labelledby="navbarDropdown">
                      <li><a className="dropdown-item" href="#">Action</a></li>
                      <li><a className="dropdown-item" href="#">Another action</a></li>
                      <li><hr className="dropdown-divider" /></li>
                      <li><a className="dropdown-item" href="#">Something else here</a></li>
                    </ul>
                  </li>
                  <li className="nav-item">
                    <a className="nav-link disabled" href="#" tabindex="-1" aria-disabled="true">Disabled</a>
                  </li>
                </ul>
                <form className="d-flex">
                  <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search" />
                  <button className="btn btn-outline-success" type="submit">Search</button>
                </form>
              </div>
            </div>
          </nav>

        <div className="container">

          <div className="row">
            <div className="">
              <Header category={category} changeCategory={this.changeCategory} search={this.search} ></Header>
              <div className='d-flex justify-content-between'>
                <p className='text-black-50'>
                  About {totalResults} result found
                </p>
                <p className='text-black-50 ml-auto'>
                  {currentPage} page of {totalPage}
                </p>
              </div>
              {
                this.state.isLoading ? (
                  <Loading />
                ) : (

                  <div>
                    <NewsList news={articles} />
                    <Pagination
                      next={this.next}
                      prev={this.prev}
                      isNext={isNext}
                      isPrev={isPrev}
                      totalPage={totalPage}
                      currentPage={currentPage}
                      handlePageChanege={this.handlePageChanege}
                      goTOpage={this.goTOpage}
                    />
                  </div>

                )
              }

            </div>
          </div>
        </div>
      </>

    )
  }

}

export default App;
