import React from 'react';
import Header from './component/Header';
import News, { newsCategory } from './news';
import NewsList from './component/NewsList';
import Pagination from './component/Pagination';
import Loading from './component/Loading';

const news = new News(newsCategory.technology);


class App extends React.Component {
  constructor() {
    super();
    this.state = {
      data: {},
      isLoading: true,
    }
  }
  componentDidMount() {
    news.getNews()
      .then(data => {
        this.setState({ data: data, isLoading: false })
      })
      .catch((e) => {
        console.log(e);
        alert("Something Was Wrong");
        this.setState({ isLoading: false })
      })

    // let news = new News(newsCategory.technology);
    // news.getNews().then(data =>{
    //   console.log(data);
    // })
    // const url = `${process.env.REACT_APP_NEWS_API_URL}?apikey=${process.env.REACT_APP_NEWS_API_KEY}&category=${this.state.category}&pageSize=5`;
    // axios.get(url)
    // .then(response => {  
    //   this.setState({ news: response.data.articles });
    //   console.log(this.state.news);
    // })
    // .catch(e => {
    //   console.log(e);
    // } 
    // );
  }

  next = () => {
    if(this.state.data.isNext){
      this.setState({isLoading : true});
    }
    news.next().then(data => {
      this.setState({ data: data, isLoading: false })
    } )
     .catch(e => {
      console.log(e);
      alert("Something Was Wrong");
      this.setState({ isLoading: false })
    })

  }
  prev = () => {
    if(this.state.data.isPrev){
      this.setState({isLoading : true});
    }
    news.prev().then(data => {
      this.setState({ data: data, isLoading: false })
    } )
     .catch(e => {
      console.log(e);
      alert("Something Was Wrong");
      this.setState({ isLoading: false })
    })
}


  componentDidUpdate(prevProps, prevState) {
    // if(prevState.category !== this.state.category){
    //   const url = `${process.env.REACT_APP_NEWS_API_URL}?apikey=${process.env.REACT_APP_NEWS_API_KEY}&category=${this.state.category}&pageSize=5`;
    //   axios.get(url)
    //   .then(response => {  
    //     this.setState({ news: response.data.articles });
    //     console.log(this.state.news);
    //   })
    //   .catch(e => {
    //     console.log(e);
    //   } 
    //   );
    // }
  }
  render() {

    const { 
      articles,
      totalPage,
      currentPage,
      category,
      totalResults,
      isNext,
      isPrev,
      isLoading
    } = this.state.data;

    return (
      <div className="container">
        <div className="row">
          <div className="col-sm-6 offset-md-3">
            <Header category={this.state.category} changeCategory={this.changeCategory} ></Header>
            <div className='d-flex justify-content-between'>
              <p className='text-black-50'>
                About {0} result found
              </p>
              <p className='text-black-50 ml-auto'>
                {1} page of {100}
              </p>
            </div>
            {
              this.state.isLoading ? (<Loading />) : (<NewsList news={this.state.data.articles} />)
            }
            <Pagination></Pagination>

          </div>
        </div>
      </div>

    )
  }

}

export default App;
