import React, { Component } from 'react';

class Nav extends Component {
  state = { 
    SearchTeam: ''
 }
handleChange = (event) => {
this.setState({SearchTeam: event.target.value});
}

handleKeyPress = (event) => {
if(event.key === 'Enter') {
    this.props.search(this.state.SearchTeam);
}
}
handleclick = (event) => {

    this.props.search(this.state.SearchTeam);

}
    render() {
      console.log(this.state.SearchTeam)
        return (
            <div>
                 <nav className="navbar navbar-expand-lg navbar-dark bg-dark fixed-top">
          <div className="container-fluid">
            <a className="navbar-brand" href="#">Yearul Islam</a>
            <button className="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarSupportedContent">
              <ul className="navbar-nav me-auto mb-2 mb-lg-0">
                <li className="nav-item">
                  <a className="nav-link active" aria-current="page" href="#">Home</a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="#">Link</a>
                </li>
                <li className="nav-item dropdown">
                  <a className="nav-link dropdown-toggle" href="#" id="navbarDropdown" role="button" data-bs-toggle="dropdown" aria-expanded="false">
                    Dropdown
                  </a>
                  <ul className="dropdown-menu" aria-labelledby="navbarDropdown">
                    <li><a className="dropdown-item" href="#">Action</a></li>
                    <li><a className="dropdown-item" href="#">Another action</a></li>
                    <li><hr className="dropdown-divider" /></li>
                    <li><a className="dropdown-item" href="#">Something else here</a></li>
                  </ul>
                </li>
                <li className="nav-item">
                  <a className="nav-link disabled" href="#" tabindex="-1" aria-disabled="true">Disabled</a>
                </li>
              </ul>
                <div className='d-flex'>
                <input className="form-control me-2" type="search" placeholder="Search" 
                value={this.state.SearchTeam} 
                onChange= {this.handleChange} 
                onKeyPress={this.handleKeyPress}             
                 />
                <button className="btn btn-outline-success" type="submit" onClick={this.handleclick}>Search</button>
                </div>
            </div>
          </div>
        </nav>

            </div>
        );
    }
}

export default Nav;
