import React, { Component } from 'react'

 class Pagination extends Component {
     state ={
         isEditable: false,
     }
  render() {
    const {    
        next,
        prev,
        totalPage,
        currentPage,
        isNext,
        isPrev,
      } = this.props;
    return (
      <div className='d-flex my-5 align-items-center'>
          <button className='btn btn-warning' disabled={!isPrev} onClick={() => prev()}>Previous</button>
          <div className='flex-grow-1 text-center'>
              {this.state.isEditable ? (<input type='number' value='1'/>):(
                  <p style={{ usetSelect: 'none', lineHeight: '1.1'}} 
                  title='Double Tap to Jump Page'
                  onDoubleClick={() =>{
                      this.setState({isEditable: !this.state.isEditable})
                  }}>
                      {1} of {100}
                      <br/>
                      <small>Double Click to Edit</small>
                  </p>
              )} 
          </div>
          <button className='btn btn-warning'
           disabled={!isNext}
              onClick={() => next()}
           >Next</button>
        
      </div>
    )
  }
}

export default Pagination
